        public static Firework BurstWhiteFlickerTrail = new Firework(30, 2, FireworkShapes.Burst, true, true, MCColors.WHITE, MCColors.WHITE);
        public static Firework BurstYellowFlickerTrail = new Firework(30, 2, FireworkShapes.Burst, true, true, MCColors.YELLOW, MCColors.YELLOW);

        public static string GetCommand(Firework firework)
        {
            string command = "{LifeTime:" + LifeTimeToString(firework) + ",FireworksItem:{id:firework_rocket,Count:1,tag:{Fireworks:{Flight:" 
                + FlightDurationToString(firework) + ",Explosions:[{Type:" + ShapeToString(firework) + ",Flicker:" + FlickerToString(firework) 
                + ",Trail:" + TrailToString(firework) + ",Colors:[I;" + ExplosionColorsToString(firework) + "],FadeColors:[I;" + FadeColorsToString(firework) + "]}]}}}}";
            Console.WriteLine(command);
            return command;
        }

        private static string LifeTimeToString(Firework firework)
        {
            return firework.LifeTime.ToString();
        }

        private static string FlightDurationToString(Firework firework)
        {
            return firework.FlightDuration.ToString();
        }

        private static string ShapeToString(Firework firework)
        {
            switch (firework.Shape)
            {
                case FireworkShapes.SmallBall:
                    return "0";

                case FireworkShapes.LargeBall:
                    return "1";

                case FireworkShapes.Star:
                    return "2";

                case FireworkShapes.Creeper:
                    return "3";

                case FireworkShapes.Burst:
                    return "4";
                default:
                    return "0";
            }
        }

        private static string FlickerToString(Firework firework)
        {
            return firework.Flicker == true ? "1" : "0";
        }

        private static string TrailToString(Firework firework)
        {
            return firework.Trail == true ? "1" : "0";
        }

        private static string ExplosionColorsToString(Firework firework)
        {
            string returnColors = "";
            int Count = firework.ExplosionColors.Count;

            if(firework.ExplosionColors.Count > 1)
            {
                for(int i = 0; i < Count; i++)
                {
                    if(Count == i + 1)
                    {
                        returnColors += ColorConvert(firework.ExplosionColors[i]);
                        break;
                    }
                    else
                    {
                        returnColors += ColorConvert(firework.ExplosionColors[i]) + ",";
                    }

                }
                return returnColors;
            }
            else
            {
                return ColorConvert(firework.ExplosionColors[0]);
            }
        }

        private static string FadeColorsToString(Firework firework)
        {
            string returnColors = "";
            int Count = firework.FadeColors.Count;

            if (firework.FadeColors.Count > 1)
            {
                for (int i = 0; i < Count; i++)
                {
                    if (Count == i + 1)
                    {
                        returnColors += ColorConvert(firework.FadeColors[i]);
                        break;
                    }
                    else
                    {
                        returnColors += ColorConvert(firework.FadeColors[i]) + ",";
                    }

                }
                return returnColors;
            }
            else
            {
                return ColorConvert(firework.FadeColors[0]);
            }
        }

        private static string ColorConvert(MCColors color)
        {
            switch (color)
            {
                case MCColors.BLACK:
                    return "1973019";
                case MCColors.BLUE:
                    return "2437522";
                case MCColors.BROWN:
                    return "5320730";
                case MCColors.CYAN:
                    return "2651799";
                case MCColors.GRAY:
                    return "4408131";
                case MCColors.GREEN:
                    return "3887386";
                case MCColors.LIGHTBLUE:
                    return "6719955";
                case MCColors.LIGHTGRAY:
                    return "11250603";
                case MCColors.LIME:
                    return "4312372";
                case MCColors.MAGENTA:
                    return "12801229";
                case MCColors.ORANGE:
                    return "15435844";
                case MCColors.PINK:
                    return "14188952";
                case MCColors.PURPLE:
                    return "8073150";
                case MCColors.RED:
                    return "11743532";
                case MCColors.WHITE:
                    return "15790320";
                case MCColors.YELLOW:
                    return "14602026";
                default:
                    return "0";
            }
        }
    }
